        private EnvComponent CreateEnvComponent(Type type, int ressLibel, int ressBeforeDel, int ressAfterDel, bool createModifTooltipVisible, bool hasHelp)
        {
            // Add the new component
            EnvComponent cmp = new()
            {
                Name = type.Name + "Component",
                URL = "evapi/" + type.Name.ToLower(),
                Ref = "Ref" + type.Name,
                FormClass = "ev-dataform-container",
                RessLibel = ressLibel,
                RessBeforeDel = ressBeforeDel,
                RessAfterDel = ressAfterDel,
                CreateModifTooltipVisible = createModifTooltipVisible,
                HasHelp = hasHelp,
                // List DateTime? properties so that we can convert them into Moments after the client application call
                Moments = String.Join(",", type.GetProperties()
                    .Where(x => x.Name != "DModif" && x.PropertyType == typeof(DateTime?))
                    .Select(x => x.Name))
            };
            if (CurrentCulture.Name == "en-GB")
            {
                cmp.Libelle = DbContext.Ressources.Where(e => e.RefRessource == ressLibel).FirstOrDefault()?.LibelleENGB;
            }
            else
            {
                cmp.Libelle = DbContext.Ressources.Where(e => e.RefRessource == ressLibel).FirstOrDefault()?.LibelleFRFR;
            }
            EnvComponents[type.Name + "Component"] = cmp;
            return cmp;
        }
