        private EnvMenu CreateEnvMenu(CulturedRessources cR, MenuName name, int ress, ModuleName module, string cmt)
        {
            // Check ressource availability
            string cultured = cR.GetTextRessource(ress);
            if (cultured == "")
                cultured = "?" + name.ToString() + "?";
            // Add the new menu
            EnvMenu menu = new EnvMenu()
            {
                Name = name.ToString(),
                RefRessource = ress,
                CulturedCaption = cultured,
                Module = EnvModules[module.ToString()],
                Cmt = cmt,
            };
            EnvMenus[name.ToString()] = menu;
            return menu;
        }
        /// <summary>
        /// Automates the grid component behavior for the given menu
        /// </summary>
        private void SetMenuGrid(EnvMenu menu, DataColumnName gridField, bool gridSort, string gridGoto, string GridRef)
        {
            menu.GridField = gridField.ToString();
            menu.GridGoto = gridGoto;
            menu.GridSort = gridSort;
            menu.GridRef = GridRef;
        }
